html.script:
function updateVideoFeed() {
    $.get( "/feed/${feed.owner.username}/${feed.name}/", function( xml ) {
      xmlDoc = $.parseXML( xml );
      xml = $( xmlDoc );
      url = xml.find("url").text();
      vf = $( "#videoFrame" );
      if (vf.attr("src") != url) {
          console.log("New video URL " + url);
          vf.attr("src", url);
      }
      setTimeout(updateVideoFeed, 5000);
    });
}
updateVideoFeed();
